PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE schedule (id INTEGER PRIMARY KEY, date INTEGER, time INTEGER, availability varchar(255), booked varchar(255), user varchar(255));
CREATE TABLE reviews (id INTEGER PRIMARY KEY, review varchar(10000), author varchar(255));
CREATE TABLE gifts (id INTEGER PRIMARY KEY, username varchar(255), code varchar(255), status varchar (255), validation varchar (255), duration varchar(255));
CREATE TABLE users (id INTEGER PRIMARY KEY, username varchar(255), hash varchar(255), date DATETIME, admin BOOLEAN, email varchar(255), validation varchar(255), reset varchar(255), timestamp DATETIME);
COMMIT;
